class Fib:
    def \_\_init\_\_(self):
        self.prev = 0
        self.curr = 1
    def \_\_iter\_\_(self):
        return self
    def \_\_next\_\_(self):
        self.curr, self.prev = self.prev + self.curr, self.curr
        return self.curr

def main():
    fib = Fib()
    fib_list = \[\]
    for _ in range(10):
        fib_list.append(next(fib))
    print(fib_list)
