    import os
    from random import randint

    def create_list(length=10):
        return [ randint(0,10) for _ in range(length) ]

    def wrapper(func):
        def wrapper(*arg, **kwargs):
            # --- 附加功能 ---
            print('calling{}..'.format(func.__name__))
            return func(*arg, **kwargs)
        return wrapper

    @wrapper
    def sum_normal(l):
        sum_for_list = 0
        for i in l:
            sum_for_list += i
        return sum_for_list

    @wrapper
    def sum_recursion(l):
        if l == []:
            return 0
        return l[0] + sum_recursion(l[1:])

    def main():
        l = create_list(3)    
        print('list:{l}\nsum:{sum}'.format(l=l,sum=sum_normal(l)))
        print('-'*80)
        print('list:{l}\nsum:{sum}'.format(l=l,sum=sum_recursion(l)))
    if __name__ == "__main__":
        main()
