    $ch = curl_init();
    curl\_setopt($ch, CURLOPT\_URL,$url);
    curl\_setopt($ch, CURLOPT\_RETURNTRANSFER,1);//不输出内容
    curl\_setopt($ch, CURLOPT\_SSL_VERIFYPEER, false);
