//方法一  
function bianli($dir)  
{  
 if(!is_dir($dir)) return;  
 $files = array();  
 if($handle = opendir($dir)){  
  while(false !== ($file = readdir($handle))){  
   if($file != '.' && $file != '..'){  
    if(is_dir($dir.'/'.$file)){  
     $files\[$file\] = bianli($dir.'/'.$file);  
    }else {  
     $files\[\] = $file;  
    }   
   }  
  }  
 }  
 closedir($handle);  
 return $files;  
}  
//方法二  
function getdir($path)  
{  
 if(!is_dir($path)) return;  
 $handle = dir($path);  
 $files = array();  
 while($file=$handle->read())  
 {  
  if($file!='.' && $file!='..')  
  {  
   $path2 = $path.'/'.$file;  
   if(is_dir($path2))  
   {  
    $files\[$file\] = getdir($path2);  
   }else  
   {  
    $files\[\] = $file;  
   }  
  }  
 }  
 $handle->close();  
 return $files;  
}  
//方法三  
function get\_dir\_scandir($path)  
{  
    if(!is_dir($path)) return;  
 $tree = array();  
 foreach(scandir($path) as $single){  
  if($single!='.' && $single!='..')  
  {  
   $path2 = $path.'/'.$single;  
   if(is_dir($path2))  
   {  
    $tree\[$single\] = get\_dir\_scandir($path2);  
   }else  
   {  
    $tree\[\] = $single;  
   }  
  }  
 }  
 return $tree;  
}

